INSERT INTO movie (title, alias, category_id, description, release_date, duration, img_url) VALUES
('BUDDA. DZIECIAK 98', 'budda-dzieciak-98', 10, 'Opowieść o życiu i karierze polskiego influencera Kamila Labuddy, znanego jako "BUDDA". Film ukazuje jego sukcesy w social mediach, zarabianie, oraz kulisy branży. Poznaj historię jednego z najpopularniejszych twórców internetowych w Polsce.', '2024-03-21', 81, 'https://multikino.pl/-/media/images/film-and-events/2024/budda-dzieciak-98/budda-dzieciak-98_plakat_final-cut.jpg'),
('GODZILLA I KONG: NOWE IMPERIUM','godzilla-i-kong-nowe-imperium', 1, 'Walka gigantów trwa! Potężny Kong i przeraźliwy Godzilla muszą zmierzyć się z kolosalnym, nieodkrytym zagrożeniem, zagrażającym naszemu światu. Czy istnienie potworów oraz nasze jest zagrożone? "Godzilla i Kong: Nowe imperium" zagłębia się w historię tych tytanów, ich początki oraz tajemnice Wyspy Czaszki, rzucając światło na mityczną bitwę, która związała ich losy z ludzkością na zawsze.', '2024-03-28', 113, 'https://multikino.pl/-/media/images/film-and-events/2024/godzilla-i-kong-nowe-imperium/godzilla-kong-plakat-final-cut.jpg?h=477&w=319'),
('DIUNA: CZĘŚĆ DRUGA','diuna-czesc-druga', 1, 'W drugiej części widzimy imponującą podróż Paula Atrydy, który zawiera sojusz z Chani i Fremenami. Wkracza też na wojenną ścieżkę, pełen żądzy zemsty na spiskowcach, którzy doprowadzili do upadku jego rodziny. Stojąc przed wyborem między miłością życia a losem wszechświata, pragnie zapobiec tragicznej przyszłości, którą tylko on potrafi przewidzieć.', '2024-03-01', 166, 'https://multikino.pl/-/media/images/film-and-events/2024/diuna-czesc-druga/diuna2_plakat-final-cut.jpg?h=477&w=319'),
('KUNG FU PANDA 4','kung-fu-panda-4', 11, 'PO, Smoczy Wojownik, po heroicznym zwycięstwie, zostaje wezwany, aby zostać duchowym przywódcą Doliny Spokoju. Brakuje mu doświadczenia, więc musi znaleźć i wyszkolić nowego Smoczego Wojownika. Wkracza do gry czarodziejka-Kameleon, zmieniająca się w różne stworzenia. Z pomocą bystrej lisicy Zhen, próbują powstrzymać Kameleonę i ochronić dolinę. Duet odkrywa, że przyjaźń może pojawić się w najmniej oczekiwanych miejscach.', '2024-03-08', 94, 'https://multikino.pl/-/media/images/film-and-events/2024/kung-fu-panda-4/kung-fu-panda-4-plakat-online-cut.jpg?h=477&w=319'),
('CHŁOPAKI NIE PŁACZĄ', 'chlopaki-nie-placza', 3, 'Film opowiada o dwóch młodych przyjaciołach, Kubie i Oskarze, wplątanych w aferę z gangsterami. Kuba, chcąc pomóc Oskarowi, zaprasza do domu dwie dziewczyny z agencji towarzyskiej. Po szalonej nocy opiekun dziewczyn zabiera cenną figurkę z kolekcji wuja Kubusia. Aby ją odzyskać, Kuba zastawia skrzypce w lombardzie i wkracza w mafijną transakcję, co prowadzi do zamieszania i pościgu gangsterów, którzy myślą, że to on jest złodziejem.', '2024-04-25', 96, 'https://multikino.pl/-/media/images/film-and-events/offowe/chlopaki-nie-placza-plakat.jpg?h=477&w=319'),
('ZA DUŻY NA BAJKI 2','za-duzy-na-bajki-2', 11, 'Ulubione gamingowe trio - Waldek, Staszek i Delfina - spędza pełne przygód lato w Tatrach. Waldek odkrywa, że jego ojciec mieszka w okolicy i postanawia go odnaleźć, dowiedzieć się o przyczyny porzucenia rodziny. Razem z Staszkiem wyruszają na poszukiwania. Delfina ma za zadanie ukryć prawdę przed rodziną Waldka. Jakie zawirowania przyniesie ta misja? Czy Waldek znajdzie odpowiedzi od ojca?', '2024-03-15', 95, 'https://multikino.pl/-/media/images/film-and-events/2024/za-duzy-na-bajki-2/zaduzynabajki2_plakat_cut.jpg?h=477&w=319'),
('CIVIL WAR', 'civil-war', 1, 'Nominowany do Oscara Alex Garland to jeden z najciekawszych wizjonerskich reżyserów współczesnego kina. W swoim najnowszym filmie przeprowadza widzów przez wstrząsające doświadczenie. Przedstawia przerażającą, dystopijną wizję jutra. Wkrótce skończy się świat jaki znamy, a już teraz zaczyna się nowy rozdział w kinie. Co się stanie jeśli ludzie nie zmienią nastawienia do "innych"?', '2024-04-12', 148, 'https://multikino.pl/-/media/images/film-and-events/2024/civil-war/civilwar_b1_plakat-final-cut.jpg?h=477&w=319');
